global.set("tempOptimal", parseInt(tempOptimal));

msg.payload = tempOptimal;

return msg;

var split = msg.payload.split(" ");

var tempRadius = split[1];

global.set("tempRadius", parseInt(tempRadius));

msg.payload = tempRadius;

return msg;

var split = msg.payload.split(" ");

var airHumOptimal = split[2];

global.set("airHumOptimal", parseInt(airHumOptimal));

msg.payload = airHumOptimal;

return msg;

var split = msg.payload.split(" ");

var airHumRadius = split[3];

global.set("airHumRadius", parseInt(airHumRadius));

msg.payload = airHumRadius;

return msg;

var split = msg.payload.split(" ");

var soilHumOptimal = split[4];

global.set("soilHumOptimal", parseInt(soilHumOptimal));

msg.payload = soilHumOptimal;

return msg;

var split = msg.payload.split(" ");

var soilHumRadius = split[5];

global.set("soilHumRadius", parseInt(soilHumRadius));

msg.payload = soilHumRadius;

return msg;

var table = global.get("savedData") || [];
var seconds = (global.get("cycleSeconds") || 0) * 1000;
var minutes = (global.get("cycleMinutes") || 0) * 60000;
var hours = (global.get("cycleHours") || 0) * 86400000;

var tableTreshold = seconds + minutes + hours;

if(tableTreshold < 5000) {
    tableTreshold = 5000;
}

const now = new Date();

if(table.length > 0) {
    const dateLastSplit = table[0]["date"].split(" ");

    const dateLast = Date.parse(
        dateLastSplit[0] 
        + "T" + 
        dateLastSplit[1]
    );

    if(Math.abs(Date.parse(now) - dateLast) < tableTreshold) {
        return null;
    }
}

const date = new String(
    now.getFullYear() + "-" + 
    (now.getMonth() + 1).toString().padStart(2, '0') + "-" + 
    now.getDate().toString().padStart(2, '0') + "T" + 
    now.getHours().toString().padStart(2, '0') + ":" + 
    now.getMinutes().toString().padStart(2, '0') + ":" +
    now.getSeconds().toString().padStart(2, '0') + ".000Z"
);

const dateSplit = date.split("T");

table.unshift({
    "date": dateSplit[0] + " " + dateSplit[1].substring(0,dateSplit[1].length - 5),
    "temperature": msg.payload["esp32/temperature"],
    "air_humidity": msg.payload["esp32/airHumidity"],
    "soil_humidity": msg.payload["esp32/soilHumidity"],
    "light": msg.payload["esp32/light"],
});

while(table.length > 60) {
    table.pop();
}

msg.payload = table;

return msg;

msg.payload = global.get("savedData") || [];

var now = new Date();

msg.filename = "/home/raspberry/node-red-logs/" + 
    now.getFullYear() + "-" + 
    (now.getMonth() + 1).toString().padStart(2, '0') + "-" + 
    now.getDate().toString().padStart(2, '0') + "T" + 
    now.getHours().toString().padStart(2, '0') + ":" + 
    now.getMinutes().toString().padStart(2, '0') + ":" +
    now.getSeconds().toString().padStart(2, '0') + ".csv";

return msg;

var split = msg.payload.split(" ");

var temp = split[0] + "/" + split[1];
var airHumidity = split[2] + "/" + split[3];
var soilHumidity = split[4] + "/" + split[5];

msg.temp = temp;
msg.airHumidity = airHumidity;
msg.soilHumidity = soilHumidity;

return msg;

if (msg.payload) {
    msg.payload = "ONLINE";
    if (global.get("status") == "ONLINE") {
        return;
    } else {
        global.set("status", "ONLINE");
    }

    return [msg, null];
} else if (!msg.payload) {
    msg.payload = "OFFLINE";
    if (global.get("status") == "OFFLINE") {
        return;
    } else {
        global.set("status", "OFFLINE");
    }

    return [null, msg];
}

if(!global.get("tempOptimal"))
    global.set("tempOptimal", 22);

if(!global.get("tempRadius"))
    global.set("tempRadius", 1);  

if (!global.get("airHumOptimal"))
    global.set("airHumOptimal", 50);

if (!global.get("airHumRadius"))
    global.set("airHumRadius", 5);

if (!global.get("soilHumOptimal"))
    global.set("soilHumOptimal", 50);

if (!global.get("soilHumRadius"))
    global.set("soilHumRadius", 5);

msg.payload = 
    global.get("tempOptimal") + " " + 
    global.get("tempRadius") + " " + 
    global.get("airHumOptimal") + " " + 
    global.get("airHumRadius") + " " + 
    global.get("soilHumOptimal") + " " + 
    global.get("soilHumRadius");

return msg;

if(msg.payload) {
    global.set("syncMode", "auto");
} else {
    global.set("syncMode", "manual");
}

return msg;
